import { createRouter, createWebHashHistory } from 'vue-router'
import Login from '../views/login/Login'
import Shop from '../views/shop/Shop'
import Register from '../views/register/Register'
const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    component: () => import(/* webpackChunkName: "hemo" */ '../views/home/Home')
  },
  {
    // 商家详情
    path: '/shop',
    name: 'Shop',
    component: Shop 
  },
  {
    path: '/login',
    name: 'Login',
    component: Login,
    // beforeEnter访问Login这个也面前才会执行
    beforeEnter(to, from, next) {
      // 真执行前面假执行后面
      // const {isLogin}= localStorage;
      localStorage.isLogin ? next({ name: 'Home' }) : next()
    }
  },
  {
    path: '/register',
    name: 'Register',
    component: Register,
    beforeEnter(to, from, next) {
      localStorage.isLogin ? next({ name: 'Home' }) : next()
    }
  },
  // {
  //   path: '/about',
  //   name: 'about',
  //   // route level code-splitting
  //   // this generates a separate chunk (about.[hash].js) for this route
  //   // which is lazy-loaded when the route is visited.
  //   component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue')
  // }
]

const router = createRouter({
  history: createWebHashHistory(),
  routes
})

// router.beforeEach 每次跳转前都会执行
// to 指跳转哪里的信息
// from 从哪里跳转来的信息
router.beforeEach((to, from, next) => {
  (localStorage.isLogin || (to.name === "Login"||to.name === "Register")) ? next() : next({ name: 'Login' });
})
export default router
